mapper.select:
select a.*,b.link from xhgmnet_bs_station as a left join view_bsstatus
as b on a.bsId=b.bsId where 1=1

	and a.type=#{type}


	and a.status=#{status}


	and a.zone=#{zone}


	and a.bsId in(select bsId from view_bsstatus where
	link=#{link})

order by a.bsId asc

select a.bsId,a.name,a.status,a.type,a.level,a.period,a.zone,b.link,c.userSum,e.groupSum,d.deviceAlarmSum from xhgmnet_bs_station as a left join view_bsstatus as b on a.bsId=b.bsId left join (select count(distinct userId) as userSum ,bsId from xhgmnet_operations_userstatus group by bsId) as c on a.bsId=c.bsId
left join (select count(gourpId) as groupSum ,bsId from xhgmnet_bs_groupstatus group by bsId) as e on a.bsId=e.bsId
left join (select bsId,sum(alarmSum) as deviceAlarmSum from ( select bsId, count(working) as alarmSum from xhgmnet_bs_bsc where working>0 and working!=255 group by bsId union all select a.bsId,count(a.bsrId) as alarmSum from xhgmnet_bs_bsr_config as a left join xhgmnet_bs_bsr as b on a.bsId=b.bsId and a.bsrId=b.Id where a.bsrIsEnable =1 and b.working>0 and b.working !=255 group by a.bsId union all select bsId, count(working) as alarmSum from xhgmnet_bs_dpx where working>0 and working!=255 group by bsId union all select bsId, count(working) as alarmSum from xhgmnet_bs_psm where dc12=1 or dc301=1 or dc302=1 or runStatus=1 or tempStatus=1 or linkStatus=1 group by bsId ) as temp group by bsId) as d on a.bsId=d.bsId
where 1=1
and a.level=#{type}
and a.status=#{status}
and a.zone=#{zone}
and a.bsId in(select bsId from view_bsstatus where link=#{link})
and a.bsId in (select bsId from xhgmnet_operations_userstatus where userId=${usergroup} or TGid=${usergroup})
and a.bsId in (
#{id}
)
order by a.bsId asc

select a.*,b.name as bsName from xhgmnet_bs_link_config as a
left join
xhgmnet_bs_station as b on a.bsId=b.bsId where a.bsId=#{bsId}

