SELECT T1.*, T2.call_sort, T3.user_sort, T4.queue_sort FROM ( select x.*,y.`name`,y.`level`,y.area,y.zone from (select bsid, sum(TotalActiveCalls) AS TotalActiveCalls, sum(TotalActiveCallDuration) AS TotalActiveCallDuration, round(avg(AverageCallDuration), 0) AS AverageCallDuration, sum(PTTCount) AS PTTCount, sum(QueueCount) AS QueueCount, sum(QueueDuration) AS QueueDuration, max(MaxUserRegCount) AS MaxUserRegCount, max(MaxGroupRegCount) AS MaxGroupRegCount from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
where
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
GROUP BY bsid) as x left join xhgmnet.xhgmnet_bs_station as y on x.bsid=y.bsId ) AS T1
LEFT JOIN ( SELECT a.bsid, CASE WHEN @rowtotal = a.TotalActiveCalls THEN @rownum WHEN @rowtotal := a.TotalActiveCalls THEN @rownum :=@rownum + 1 WHEN @rowtotal = 0 THEN @rownum :=@rownum + 1 END AS call_sort FROM ( SELECT bsid, sum(TotalActiveCalls) AS TotalActiveCalls FROM
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
WHERE
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
GROUP BY bsid ORDER BY TotalActiveCalls DESC ) AS a, ( SELECT @rownum := 0 ,@rowtotal := NULL ) r ) AS T2 ON T1.bsid = T2.bsid
LEFT JOIN ( SELECT a.bsid, CASE WHEN @rowtotal1 = a.MaxUserRegCount THEN @rownum1 WHEN @rowtotal1 := a.MaxUserRegCount THEN @rownum1 :=@rownum1 + 1 WHEN @rowtotal1 = 0 THEN @rownum1 :=@rownum1 + 1 END AS user_sort FROM ( SELECT bsid, max(MaxUserRegCount) as MaxUserRegCount FROM
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
WHERE
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
GROUP BY bsid ORDER BY MaxUserRegCount DESC ) AS a, ( SELECT @rownum1 := 0 ,@rowtotal1 := NULL ) r ) AS T3 ON T1.bsid = T3.bsid
LEFT JOIN ( SELECT a.bsid, CASE WHEN @rowtotal2 = a.QueueCount THEN @rownum2 WHEN @rowtotal2 := a.QueueCount THEN @rownum2 :=@rownum2 + 1 WHEN @rowtotal2 = 0 THEN @rownum2 :=@rownum2 + 1 END AS queue_sort FROM ( SELECT bsid, sum(QueueCount) AS QueueCount FROM
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
WHERE
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
GROUP BY bsid ORDER BY QueueCount DESC ) AS a, ( SELECT @rownum2 := 0 ,@rowtotal2 := NULL ) r ) AS T4 ON T1.bsId = T4.bsid ORDER BY T1.bsid ASC

SELECT x.vpnId, x.`name`, y.TotalActiveCalls, y.TotalActiveCallDuration, y.AverageCallDuration, y.dexTotalCalls, y.TotalFailedCalls, y.FailedPercentage, y.NoEffectCalls
FROM xhgmnet_vpn AS x LEFT JOIN ( SELECT vpnid, sum(TotalActiveCalls) AS TotalActiveCalls, sum(TotalActiveCallDuration) AS TotalActiveCallDuration, round(avg(AverageCallDuration), 0) AS AverageCallDuration, sum(dexTotalCalls) AS dexTotalCalls, sum(TotalFailedCalls) AS TotalFailedCalls, FailedPercentage, sum(NoEffectCalls) AS NoEffectCalls FROM
xhgmnet_gps_voice.chart_day_vpntotal where starttime between #{time} and #{endtime}
xhgmnet_gps_voice.chart_week_vpntotal where starttime=#{time}
xhgmnet_gps_voice.chart_month_vpntotal where starttime=#{time}
xhgmnet_gps_voice.pef_newvpntotal where date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
group by vpnid) AS y ON x.vpnId = y.vpnid ORDER BY x.vpnid ASC

select y.`level`, count(x.bsid) as bsTotals, sum(x.TotalActiveCalls) as TotalActiveCalls, round((sum(x.TotalActiveCallDuration)/60),0) as TotalActiveCallDuration, round(avg(x.AverageCallDuration),0) as AverageCallDuration, sum(x.PTTCount) as PTTCount, sum(x.QueueCount) as QueueCount, sum(x.QueueDuration) as QueueDuration, max(x.MaxUserRegCount) as MaxUserRegCount, max(x.MaxGroupRegCount) as MaxGroupRegCount from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
as x left join xhgmnet.xhgmnet_bs_station as y on x.bsid=y.bsId where
x.starttime between #{time} and #{endtime}
x.starttime=#{time}
x.starttime=#{time}
date_format(x.starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
and y.level>0 group by y.`level` order by y.level asc

select y.area, count(x.bsid) as bsTotals, sum(x.TotalActiveCalls) as TotalActiveCalls, round((sum(x.TotalActiveCallDuration)/10),0) as TotalActiveCallDuration, round(avg(x.AverageCallDuration),0) as AverageCallDuration, sum(x.PTTCount) as PTTCount, sum(x.QueueCount) as QueueCount, sum(x.QueueDuration) as QueueDuration, max(x.MaxUserRegCount) as MaxUserRegCount, max(x.MaxGroupRegCount) as MaxGroupRegCount from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
as x left join xhgmnet.xhgmnet_bs_station as y on x.bsid=y.bsId where
x.starttime between #{time} and #{endtime}
x.starttime=#{time}
x.starttime=#{time}
date_format(x.starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
and y.area is not null group by y.area

select y.zone, count(x.bsid) as bsTotals, sum(x.TotalActiveCalls) as TotalActiveCalls, sum(x.TotalActiveCallDuration) as TotalActiveCallDuration, round(avg(x.AverageCallDuration),0) as AverageCallDuration, sum(x.PTTCount) as PTTCount, sum(x.QueueCount) as QueueCount, sum(x.QueueDuration) as QueueDuration, max(x.MaxUserRegCount) as MaxUserRegCount, max(x.MaxGroupRegCount) as MaxGroupRegCount from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
as x left join xhgmnet.xhgmnet_bs_station as y on x.bsid=y.bsId where
x.starttime between #{time} and #{endtime}
x.starttime=#{time}
x.starttime=#{time}
date_format(x.starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
and y.zone is not null and y.zone !='移动基站' group by y.zone order by zone asc

select count(x.bsid) as bsTotals, sum(x.TotalActiveCalls) as TotalActiveCalls, sum(x.TotalActiveCallDuration) as TotalActiveCallDuration, round(avg(x.AverageCallDuration),0) as AverageCallDuration, sum(x.PTTCount) as PTTCount, sum(x.QueueCount) as QueueCount, sum(x.QueueDuration) as QueueDuration, max(x.MaxUserRegCount) as MaxUserRegCount, max(x.MaxGroupRegCount) as MaxGroupRegCount,y.zone, ROUND(sum(x.TotalActiveCalls)/x.totals*100,2) as percent, x.starttime
from (select T1.bsid, T1.TotalActiveCalls, T1.TotalActiveCallDuration, T1.AverageCallDuration, T1.PTTCount, T1.QueueCount, T1.QueueDuration, T1.MaxUserRegCount, T1.MaxGroupRegCount, T2.totals, T1.starttime from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
as T1,( select sum(totals) as totals from ( select sum(a.TotalActiveCalls) as totals,b.zone from
chart_day_bstotal
chart_week_bstotal
chart_month_bstotal
chart_hour_bstotal
as a left join xhgmnet.xhgmnet_bs_station as b on a.bsid=b.bsId where
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
group by zone ) as e) as T2
) as x
left join xhgmnet.xhgmnet_bs_station as y on x.bsid=y.bsId where
x.starttime between #{time} and #{endtime}
x.starttime=#{time}
x.starttime=#{time}
date_format(x.starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
group by y.zone order by TotalActiveCalls desc limit 10

