SELECT x.*, y.`name` FROM ( SELECT a.*, ROUND( a.TotalActiveCalls / b.totals * 100, 2 ) AS percent FROM ( SELECT T1.vpnid, sum(T1.TotalActiveCalls) AS TotalActiveCalls, sum(T1.TotalActiveCallDuration) AS TotalActiveCallDuration, round( avg(T1.AverageCallDuration), 0 ) AS AverageCallDuration, sum(T1.dexTotalCalls) AS dexTotalCalls, sum(T1.TotalFailedCalls) AS TotalFailedCalls, T1.FailedPercentage, sum(T1.NoEffectCalls) AS NoEffectCalls, T1.starttime FROM
chart_day_vpntotal
chart_week_vpntotal
chart_month_vpntotal
pef_newvpntotal
AS T1 WHERE
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
GROUP BY vpnid ) AS a, ( SELECT sum(TotalActiveCalls) AS totals FROM
chart_day_vpntotal
chart_week_vpntotal
chart_month_vpntotal
pef_newvpntotal
WHERE
starttime between #{time} and #{endtime}
starttime=#{time}
starttime=#{time}
date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
) AS b ) AS x LEFT JOIN xhgmnet.xhgmnet_vpn AS y ON x.vpnid = y.vpnId ORDER BY x.TotalActiveCalls DESC LIMIT 10

select sum(TotalActiveCall) AS TotalActiveCall, sum(TotalActiveCallDuration) AS TotalActiveCallDuration, round(avg(AverageCallDuration), 0) AS AverageCallDuration, sum(TotalCalls) AS TotalCalls, sum(TotalFailedCalls) AS TotalFailedCalls, sum(NoEffectCalls) AS NoEffectCalls, DATE_FORMAT(starttime, '%Y-%m-%d') AS starttime, mscid, sum(totalPTTs) as totalPTTs, sum(totalQueueCount) as totalQueueCount, sum(totalQueueDuration) as totalQueueDuration, max(totalMaxReg) as totalMaxReg, max(maxRegGroup) as maxRegGroup,
round((sum(TotalFailedCalls)/sum(TotalCalls)),0) as FailedPercentage
from
chart_day_msc where starttime between #{time} and #{endtime}
chart_week_msc where starttime=#{time}
chart_month_msc where starttime=#{time}
pef_newmsctotal where date_format(starttime,'%Y-%m-%d %H:00:00') between #{time} and #{endtime}
limit 1
