mapper.select:
select * from xhgmnet_operations_work where 1=1

	and fileName like concat('%',#{fileName},'%')


	and contact like concat('%',#{contact},'%')


	and status=#{status}

order by id asc limit #{start},#{limit}

mapper.insert:
insert into xhgmnet_operations_quality (id, fileName, contact,
tel, note, status, filePath,
uploadUser, recvUser, createtime
)
values (#{id,jdbcType=INTEGER}, #{filename,jdbcType=VARCHAR},
#{contact,jdbcType=VARCHAR},
#{tel,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}, #{status,jdbcType=BIT},
#{filepath,jdbcType=VARCHAR},
#{uploaduser,jdbcType=VARCHAR}, #{recvuser,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
)
